import React, { useState, useEffect } from "react";
import {
  Box,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Modal,
  TextField,
  Stack,
  Chip,
  IconButton,
  MenuItem,
  Grid,
} from "@mui/material";
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
} from "@mui/icons-material";
import axios from "axios";

const modalStyle = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: { xs: "90%", sm: "80%", md: 600 },
  maxHeight: "90vh",
  overflow: "auto",
  bgcolor: "background.paper",
  boxShadow: 24,
  p: 3,
  borderRadius: 2,
};

const Books = () => {
  const [books, setBooks] = useState([]);
  const [open, setOpen] = useState(false);
  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    ISBN: "",
    quantity: "",
    category: "",
    description: "",
    status: "available",
  });
  const [editOpen, setEditOpen] = useState(false);
  const [selectedBook, setSelectedBook] = useState(null);

  const handleOpen = () => setOpen(true);
  const handleClose = () => {
    setOpen(false);
    setNewBook({
      title: "",
      author: "",
      ISBN: "",
      quantity: "",
      category: "",
      description: "",
      status: "available",
    });
  };

  const handleEditOpen = (book) => {
    setSelectedBook(book);
    setEditOpen(true);
  };

  const handleEditClose = () => {
    setEditOpen(false);
    setSelectedBook(null);
  };

  const handleChange = (e) => {
    setNewBook({
      ...newBook,
      [e.target.name]: e.target.value,
    });
  };

  const handleEditChange = (e) => {
    setSelectedBook({
      ...selectedBook,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:5000/api/books",
        newBook,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setBooks([...books, response.data]);
      handleClose();
    } catch (error) {
      console.error("Kitap eklenirken hata:", error);
    }
  };

  const handleEdit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `http://localhost:5000/api/books/${selectedBook._id}`,
        selectedBook,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setBooks(
        books.map((book) =>
          book._id === selectedBook._id ? response.data : book
        )
      );
      handleEditClose();
    } catch (error) {
      console.error("Kitap düzenlenirken hata:", error);
    }
  };

  const handleDelete = async (book) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/books/${book._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setBooks(books.filter((b) => b._id !== book._id));
    } catch (error) {
      console.error("Kitap silinirken hata:", error);
    }
  };

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/books");
        setBooks(response.data);
      } catch (error) {
        console.error("Kitaplar yüklenirken hata:", error);
      }
    };

    fetchBooks();
  }, []);

  // Durum çevirileri için yardımcı fonksiyon
  const getStatusText = (status) => {
    switch (status) {
      case "available":
        return "Müsait";
      case "borrowed":
        return "Ödünç Verildi";
      case "reserved":
        return "Rezerve";
      default:
        return "Bilinmiyor";
    }
  };

  // Durum rengini belirleyen fonksiyon
  const getStatusColor = (status) => {
    switch (status) {
      case "available":
        return "success";
      case "borrowed":
        return "error";
      case "reserved":
        return "warning";
      default:
        return "default";
    }
  };

  return (
    <Box>
      <Box sx={{ display: "flex", justifyContent: "space-between", mb: 3 }}>
        <Typography variant="h4">Kitaplar</Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={handleOpen}
        >
          Yeni Kitap Ekle
        </Button>
      </Box>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Kitap Adı</TableCell>
              <TableCell>Yazar</TableCell>
              <TableCell>ISBN</TableCell>
              <TableCell>Yayın Yılı</TableCell>
              <TableCell>Stok</TableCell>
              <TableCell>Durum</TableCell>
              <TableCell>İşlemler</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {books.map((book) => (
              <TableRow key={book._id}>
                <TableCell>{book.title}</TableCell>
                <TableCell>{book.author}</TableCell>
                <TableCell>{book.isbn}</TableCell>
                <TableCell>{book.publishYear}</TableCell>
                <TableCell>{book.quantity}</TableCell>
                <TableCell>
                  <Chip
                    label={getStatusText(book.status)}
                    color={getStatusColor(book.status)}
                    size="small"
                  />
                </TableCell>
                <TableCell>
                  <IconButton
                    size="small"
                    color="primary"
                    onClick={() => handleEditOpen(book)}
                  >
                    <EditIcon />
                  </IconButton>
                  <IconButton
                    size="small"
                    color="error"
                    onClick={() => handleDelete(book)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Modal open={editOpen} onClose={handleEditClose}>
        <Box sx={modalStyle}>
          <Typography variant="h6" component="h2" mb={3}>
            Kitap Düzenle
          </Typography>
          <form onSubmit={handleEdit}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="title"
                  label="Kitap Adı"
                  value={selectedBook?.title || ""}
                  onChange={handleEditChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="author"
                  label="Yazar"
                  value={selectedBook?.author || ""}
                  onChange={handleEditChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="isbn"
                  label="ISBN"
                  value={selectedBook?.isbn || ""}
                  onChange={handleEditChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="publishYear"
                  label="Yayın Yılı"
                  type="number"
                  value={selectedBook?.publishYear || ""}
                  onChange={handleEditChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="quantity"
                  label="Stok Miktarı"
                  type="number"
                  value={selectedBook?.quantity || ""}
                  onChange={handleEditChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="status"
                  label="Durum"
                  select
                  value={selectedBook?.status || "available"}
                  onChange={handleEditChange}
                  required
                  fullWidth
                >
                  <MenuItem value="available">Müsait</MenuItem>
                  <MenuItem value="borrowed">Ödünç Verildi</MenuItem>
                  <MenuItem value="reserved">Rezerve</MenuItem>
                </TextField>
              </Grid>
              <Grid item xs={12}>
                <Box
                  sx={{
                    display: "flex",
                    gap: 2,
                    justifyContent: "flex-end",
                    mt: 2,
                  }}
                >
                  <Button variant="outlined" onClick={handleEditClose}>
                    İptal
                  </Button>
                  <Button type="submit" variant="contained">
                    Güncelle
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </form>
        </Box>
      </Modal>

      <Modal open={open} onClose={handleClose}>
        <Box sx={modalStyle}>
          <Typography variant="h6" component="h2" mb={3}>
            Yeni Kitap Ekle
          </Typography>
          <form onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="title"
                  label="Kitap Adı"
                  value={newBook.title}
                  onChange={handleChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="author"
                  label="Yazar"
                  value={newBook.author}
                  onChange={handleChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="ISBN"
                  label="ISBN"
                  value={newBook.ISBN}
                  onChange={handleChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="publishYear"
                  label="Yayın Yılı"
                  type="number"
                  value={newBook.publishYear}
                  onChange={handleChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="quantity"
                  label="Stok Miktarı"
                  type="number"
                  value={newBook.quantity}
                  onChange={handleChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="status"
                  label="Durum"
                  select
                  value={newBook.status}
                  onChange={handleChange}
                  required
                  fullWidth
                >
                  <MenuItem value="available">Müsait</MenuItem>
                  <MenuItem value="borrowed">Ödünç Verildi</MenuItem>
                  <MenuItem value="reserved">Rezerve</MenuItem>
                </TextField>
              </Grid>
              <Grid item xs={12}>
                <Box
                  sx={{
                    display: "flex",
                    gap: 2,
                    justifyContent: "flex-end",
                    mt: 2,
                  }}
                >
                  <Button variant="outlined" onClick={handleClose}>
                    İptal
                  </Button>
                  <Button type="submit" variant="contained">
                    Kaydet
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </form>
        </Box>
      </Modal>
    </Box>
  );
};

export default Books;
